char findTheDifference(char * s, char * t){
    //不能用下面的簡單迴圈來比對, 因為字串的內容有被 random shuffling打亂
    //int i=0;
    //for(; s[i]!=0; i++){
    //    if(s[i]!=t[i]) return t[i];
    //}
    //return t[i];

    //所以還是要用統計的方法來判斷
    int H1[26]={};
    int H2[26]={};
    int i=0;
    for( ; s[i]!=0; i++){
        H1[s[i]-'a']++;
        H2[t[i]-'a']++;
    }
    H2[t[i]-'a']++;//因為t多1個字母

    for(int i=0; i<26; i++){
        if(H1[i]!=H2[i]) return i+'a';
    }
    return ' ';//其實不會走到這行啦,只是寫給compiler安心的, 不然compiler會抱怨 "沒有 return值"

}
