class Solution {
    public char findTheDifference(String s, String t) {
        //for(int i=0; i<s.length(); i++) {
        //    if(s.charAt(i)!=t.charAt(i)) return t.charAt(i);
        //}
        //return t.charAt(t.length()-1);
        //題目看錯，原來有 random shuffle, 所以要統計字母即可
        int [] H1 = new int[26];
        int [] H2 = new int[26];
        for(int i=0; i<s.length(); i++){
            H1[s.charAt(i)-'a']++;
        }
        for(int i=0; i<t.length(); i++){
            H2[t.charAt(i)-'a']++;
        }
        for(int i=0; i<26; i++){
            if(H1[i]!=H2[i]) return (char)('a'+i);
        }
        return 'z';
    }
}
